parameters.param:
$VCSRoot = '%teamcity.build.checkoutDir%';
$VCSRootName = 'VCSRoot';

Import-Module SqlServer

if((Get-SMBShare -Name $VCSRootName -ea 0)){
    Write-Output "Removing old $VCSRootName share"
    Remove-SmbShare -Name $VCSRootName -Force
}

Write-Output "Creating $VCSRootName share"
New-SMBShare –Name $VCSRootName –Path $VCSRoot –FullAccess "Authenticated Users"

Write-Output "Running MLX-Dependencies.ps1 Script on computer '%DataCut.SQLServer%'"
Invoke-Command -ComputerName %DataCut.SQLServer% -FilePath %teamcity.build.checkoutDir%\Common\LocalBuild\MLX-Dependencies.ps1 -ArgumentList "\\%system.agent.name%\VCSRoot\", "D:\", "D:\EDBS", "D:\DataLoad", "(local)"
